// define ADD, addMessage(), messageReducer(), and store here:

const defaultState = [];

const ADD = 'ADD';

const addMessage = (message) => {
    return {
        type: ADD,
        message: message
    }
}

const messageReducer = (state = defaultState, action) => {
    switch(action.type) {
        case(ADD):
            return [...state, action.message];
        default: 
            return state;
    }
}

const store = Redux.createStore(messageReducer);
